mapper.update:
UPDATE "daily_activity_summaries" SET
number = #{number},
amount = #{amount}
WHERE uid = #{uid}
AND year = #{year}
AND month = #{month}
AND day = #{day}

mapper.select:
SELECT * FROM "daily_activity_summaries"
WHERE uid = #{uid}
AND year = #{year}
AND month = #{month}
AND day = #{day}

SELECT day, number, amount
FROM "daily_activity_summaries"
WHERE uid = #{uid}
AND year = #{year}
AND month = #{month}
ORDER BY day ASC;
