mapper.update:
UPDATE "expense_summary" SET
amount = #{amount}
WHERE uid = #{uid}
AND year = #{year}
AND month = #{month}
AND main_category = #{mainCategory}

mapper.select:
SELECT * FROM "expense_summary"
WHERE uid = #{uid}
AND year = #{year}
AND month = #{month}
AND main_category = #{mainCategory}

SELECT main_category AS key, amount AS value FROM "expense_summary"
WHERE uid = #{uid}
AND year = #{year}
AND month = #{month}
