mapper.update:
UPDATE "monthly_expense_summary" SET
total_expense = #{totalExpense}
WHERE uid = #{uid}
AND year = #{year}
AND month = #{month}
AND main_category = #{mainCategory}

mapper.select:
SELECT * FROM "monthly_expense_summary"
WHERE uid = #{uid}
AND year = #{year}
AND month = #{month}
AND main_category = #{mainCategory}
