mapper.update:
UPDATE "subcategory_expense_summary" SET
total_expense = #{totalExpense}
WHERE uid = #{uid}
AND year = #{year}
AND month = #{month}
AND sub_category = #{subCategory}

mapper.select:
SELECT * FROM "subcategory_expense_summary"
WHERE uid = #{uid}
AND year = #{year}
AND month = #{month}
AND sub_category = #{subCategory}
