mapper.update:
UPDATE "subcategory_summaries" SET
amount = #{amount}
WHERE uid = #{uid}
AND year = #{year}
AND month = #{month}
AND sub_category = #{subCategory}

mapper.select:
SELECT * FROM "subcategory_summaries"
WHERE uid = #{uid}
AND year = #{year}
AND month = #{month}
AND sub_category = #{subCategory}

SELECT sub_category, amount
FROM "subcategory_summary"
WHERE uid = #{uid}
AND year = #{year}
AND month = #{month}
