mapper.update:
UPDATE "subcategory_summary" SET
amount = #{amount}
WHERE uid = #{uid}
AND year = #{year}
AND month = #{month}
AND sub_category = #{subCategory}

mapper.select:
SELECT * FROM "subcategory_summary"
WHERE uid = #{uid}
AND year = #{year}
AND month = #{month}
AND sub_category = #{subCategory}
